import{_ as r,c as t,o,ag as n}from"./chunks/framework.-tXfabtl.js";const u=JSON.parse('{"title":"Overview","description":"","frontmatter":{},"headers":[],"relativePath":"style-guide/overview.md","filePath":"style-guide/overview.md"}'),s={name:"style-guide/overview.md"};function i(a,e,l,g,c,p){return o(),t("div",null,e[0]||(e[0]=[n('<h1 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h1><p><strong>Atlas Design Style Guide</strong>, <strong>currently in development</strong>, aims to <strong>unify and modernize the user experience</strong> across all platforms. It was designed to meet community needs, promoting <strong>consistency, accessibility, and scalability</strong>.</p><p>These principles and guidelines help designers and developers with their <a href="http://osm.org/" target="_blank" rel="noreferrer">OSM</a> projects.</p><h2 id="our-process" tabindex="-1">Our Process <a class="header-anchor" href="#our-process" aria-label="Permalink to &quot;Our Process&quot;">​</a></h2><p><strong>Atlas</strong> is maintained through a collaborative effort between the <strong><a href="https://unb.br" target="_blank" rel="noreferrer">University of Brasília (UnB)</a>, <a href="https://ufes.br/" target="_blank" rel="noreferrer">Federal University of Espírito Santo (UFES)</a></strong>. Our team consists of <strong>passionate designers and developers</strong> dedicated to this initiative.</p><p>Our focus is on <strong>user experience design, user research, and development</strong>.</p><p><strong>Open to Participation</strong></p><p>We warmly encourage and welcome your <strong><a href="./../contributing/overview.html">collaboration and participation</a></strong> to evolve the design system. Our goal is to replicate <strong>OSM&#39;s core philosophy</strong>, being <strong>open and collaborative</strong>.</p><p>You can get involved and contribute in several ways:</p><h2 id="ways-to-contribute" tabindex="-1">Ways to contribute: <a class="header-anchor" href="#ways-to-contribute" aria-label="Permalink to &quot;Ways to contribute:&quot;">​</a></h2><p>There are many ways to <a href="./../contributing/overview.html">get involved</a>, your contributions are welcome!</p><ol><li><p><strong>Design Process</strong></p><ul><li><a href="./../contributing/designing-new-components.html">Create new components or assets</a>.</li><li><a href="./../contributing/designing-tokens.html">Create a new Design Token</a></li><li>Current design tool: <a href="https://www.figma.com/design/2vheURjyvYg2oyBeuQOxQ1/%F0%9F%92%A0-Atlas---Design-System?node-id=0-1&amp;t=v50vS8OdGh4LfwoO-1" target="_blank" rel="noreferrer"><strong>Figma</strong></a> (<em>Penpot-compatible version planned for broader accessibility</em>).</li></ul></li><li><p><strong>Code &amp; Documentation</strong></p><ul><li>Submit code improvements in our <a href="https://github.com/orgs/atlas-design-system" target="_blank" rel="noreferrer">GitHub repository</a>.</li><li>Review existing code for quality assurance.</li><li>Enhance library documentation for clarity.</li></ul></li><li><p><strong>Feedback &amp; Requests</strong></p><ul><li>Propose changes or discuss ideas in our <a href="https://github.com/orgs/atlas-design-system" target="_blank" rel="noreferrer">GitHub repository</a>.</li></ul></li></ol>',12)]))}const h=r(s,[["render",i]]);export{u as __pageData,h as default};
